import React, { useEffect, useState } from "react";
import "./FictionBooks.css";

export default function FictionBooks() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/books/fiction")
      .then((res) => res.json())
      .then((data) => setBooks(data))
      .catch((err) => console.error("Error fetching fiction books:", err));
  }, []);

  return (
    <div className="fiction-container">
      <h2>Fiction Books</h2>
      {books.length === 0 ? (
        <p>No fiction books available.</p>
      ) : (
        <ul className="fiction-list">
          {books.map((book) => (
            <li key={book.id}>
              <strong>{book.title}</strong> by {book.author} ({book.year})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
