import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Bookslists.css";

export default function FictionBooks() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/books/fiction")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setBooks(data);
        } else {
          console.error("Error: Data fetched is not an array");
          setError("Failed to load books.");
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching fiction books:", err);
        setError("An error occurred while fetching books.");
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading books...</p>;
  }

  return (
    <div className="fiction-container">
      <h2>Fiction Books</h2>
      {error && <p className="error-message">{error}</p>}

      {Array.isArray(books) && books.length === 0 ? (
        <p style={{color:'red'}}>No fiction books available at the moment.</p>
      ) : (
        <ul className="fiction-list">
          {books.map((book) => (
            <li key={book.id}>
              <Link to={`/book/${book.id}`}>
                <strong>{book.title}</strong> by {book.author}
              </Link>
            </li>
          ))}
        </ul>
      )}

      {/* ✅ Back to Home link at the bottom */}
      <div className="back-link">
        <Link to="/">← Back to Home</Link>
      </div>
    </div>
  );
}
