import React, { useState, useEffect } from "react";
import "./MainPage.css";
import { Link } from "react-router-dom";

export default function MainPage() {
  const [books, setBooks] = useState([]);  // State to store books
  const [searchTerm, setSearchTerm] = useState("");  // Track search term

  // Fetch book data (You can replace this with an API call or static data)
  useEffect(() => {
    const bookData = [
      { id: 1, title: "The Great Gatsby", author: "F. Scott Fitzgerald", url: "/fiction" },
      { id: 2, title: "A Brief History of Time", author: "Stephen Hawking", url: "/science" },
      { id: 3, title: "Sapiens: A Brief History of Humankind", author: "Yuval Noah Harari", url: "/history" },
      { id: 4, title: "Harry Potter and the Sorcerer's Stone", author: "J.K. Rowling", url: "/fantasy" },
      { id: 5, title: "Becoming", author: "Michelle Obama", url: "/biography" },
      { id: 6, title: "The Silent Patient", author: "Alex Michaelides", url: "/mystery" },
    ];

    setBooks(bookData);  // Set the books state with fetched or static data
  }, []);

  // Filter books based on search term
  const filteredBooks = books.filter((book) =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="main-container">
      <div className="header">
        <h1 style={{ color: "whitesmoke" }}>Library Books Manager</h1>
        <Link to="/addbook">
          <button className="add-button">Add New Books</button>
        </Link>
      </div>

      {/* üîç Search Bar */}
      <div className="search-section">
        <input
          type="text"
          placeholder="Search by book title..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-bar"
        />
      </div>

      {/* üìö Filtered Books */}
      <div className="books-list">
        {filteredBooks.length === 0 ? (
          <p style={{ color: "red", textAlign: "center" }}>No books match your search.</p>
        ) : (
          filteredBooks.map((book) => (
            <div key={book.id} className="book-card">
              <Link to={book.url}>
                <h3>{book.title}</h3>
                <p>by {book.author}</p>
              </Link>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
