import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Bookslists.css"; // Shared styles

export default function FantasyBooks() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchFantasyBooks = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/books/fantasy");
        const data = await res.json();

        if (Array.isArray(data)) {
          setBooks(data);
        } else {
          setError("Failed to load fantasy books.");
        }
      } catch (err) {
        console.error("Error fetching fantasy books:", err);
        setError("An error occurred while fetching fantasy books.");
      } finally {
        setLoading(false);
      }
    };

    fetchFantasyBooks();
  }, []);

  if (loading) {
    return <p>Loading fantasy books...</p>;
  }

  return (
    <div className="book-container">
      <h2>Fantasy Books</h2>
      {error && <p className="error-message">{error}</p>}
      {books.length === 0 ? (
        <p>No fantasy books available at the moment.</p>
      ) : (
        <ul className="book-list">
          {books.map((book) => (
            <li key={book.id}>
              <Link to={`/book/${book.id}`}>
                <strong>{book.title}</strong> by {book.author}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
