import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Bookslists.css";  // Import shared book styles

export default function MysteryBooks() {
  const [books, setBooks] = useState([]);  // State to store books
  const [loading, setLoading] = useState(true);  // Track loading state
  const [error, setError] = useState(""); // Track errors if any

  useEffect(() => {
    fetch("http://localhost:5000/api/books/mystery")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {  // Ensure data is an array
          setBooks(data);  // Save data into state
        } else {
          console.error("Error: Data fetched is not an array");
          setError("Failed to load books.");
        }
        setLoading(false);  // Turn off loading state
      })
      .catch((err) => {
        console.error("Error fetching mystery books:", err);
        setError("An error occurred while fetching books.");
        setLoading(false);  // Turn off loading on error
      });
  }, []);

  if (loading) {
    return <p>Loading books...</p>;  // Show loading message
  }

  return (
    <div className="fiction-container"> {/* Reuse same container class */}
      <h2>Mystery Books</h2>
      {error && <p className="error-message">{error}</p>}
      {Array.isArray(books) && books.length === 0 ? (
        <p>No mystery books available at the moment.</p>
      ) : (
        <ul className="fiction-list"> {/* Reuse list style */}
          {books.map((book) => (
            <li key={book.id}>
              <Link to={`/book/${book.id}`}>
                <strong>{book.title}</strong> by {book.author}
              </Link>
            </li>
          ))}
        </ul>
      )}

       {/* ✅ Back to Home link at the bottom */}
            <div className="back-link">
              <Link to="/">← Back to Home</Link>
            </div>
    </div>
  );
}
