import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./styles.css";  // Import the global styles here

export default function ScienceBooks() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/books/science")
      .then((res) => res.json())
      .then((data) => setBooks(data))
      .catch((err) => console.error("Error fetching science books:", err));
  }, []);

  return (
    <div className="fiction-container">
      <h2>Science Books</h2>
      <ul className="fiction-list">
        {books.map((book) => (
          <li key={book.id}>
            <Link to={`/book/${book.id}`}>
              <strong>{book.title}</strong> by {book.author}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
