import React from "react";
import "./MainPage.css";
import { Link } from "react-router-dom";

export default function MainPage() {
  const bookCategories = [
    { src: "/fiction.jpg", url: "./fiction", label: "Fiction" },
    { src: "/science.jpg", url: "./science", label: "Science" },
    { src: "/history.jpg", url: "./history", label: "History" },
    { src: "/fantasy.jpg", url: "./fantasy", label: "Fantasy" },
    { src: "/biography.jpg", url: "./biography", label: "Biography" },
    { src: "/mystery.jpg", url: "./mystery", label: "Mystery" },
  ];

  return (
    <div className="main-container">
      <div className="header">
        <h1>Book Categories</h1>
        <Link to="/addbook">
          <button className="add-button">Add Books</button>
        </Link>
      </div>

      <div className="category-grid">
        {bookCategories.map((item, index) => (
          <Link to={item.url} key={index} className="category-card">
            <img src={item.src} alt={item.label} />
            <p className="category-label">{item.label}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
