import React from 'react';

const categories = [
  { name: "Fiction", image: "https://via.placeholder.com/150", link: "/category/fiction" },
  { name: "Science", image: "https://via.placeholder.com/150", link: "/category/science" },
  { name: "History", image: "https://via.placeholder.com/150", link: "/category/history" },
  { name: "Fantasy", image: "https://via.placeholder.com/150", link: "/category/fantasy" },
  { name: "Biography", image: "https://via.placeholder.com/150", link: "/category/biography" },
  { name: "Mystery", image: "https://via.placeholder.com/150", link: "/category/mystery" },
];

const MainPage = () => {
  return (
    <div className="p-6">
      <h2>Library Books Management System</h2>
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Book Categories</h1>
        <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Add
        </button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {categories.map((cat) => (
          <a
            key={cat.name}
            href={cat.link}
            className="border rounded-lg overflow-hidden hover:shadow-lg transition"
          >
            <img
              src={cat.image}
              alt={cat.name}
              className="w-full h-40 object-cover"
            />
            <div className="p-4 text-center font-semibold text-gray-700">
              {cat.name}
            </div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default MainPage;
