import React from 'react';
import './MainPage.css'; // Import the CSS file

const categories = [
  { name: "Fiction", image: "./fiction.jpg", link: "/category/fiction" },
  { name: "Science", image: "https://via.placeholder.com/150", link: "/category/science" },
  { name: "History", image: "https://via.placeholder.com/150", link: "/category/history" },
  { name: "Fantasy", image: "https://via.placeholder.com/150", link: "/category/fantasy" },
  { name: "Biography", image: "https://via.placeholder.com/150", link: "/category/biography" },
  { name: "Mystery", image: "https://via.placeholder.com/150", link: "/category/mystery" },
];

const MainPage = () => {
  return (
    <div className="main-container">
      {/* Header */}
      <div className="header">
        <h1 className="title">Book Categories</h1>
        <button className="add-button">Add</button>
      </div>

      {/* Category Grid */}
      <div className="category-grid">
        {categories.map((cat) => (
          <a key={cat.name} href={cat.link} className="category-card">
            <img src={cat.image} alt={cat.name} className="category-image" />
            <div className="category-name">{cat.name}</div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default MainPage;
