import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";  // useHistory for redirection
import "./FictionBookDetail.css";

export default function FictionBookDetail() {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);  // State to control delete confirmation
  const history = useHistory();  // History object for navigation

  useEffect(() => {
    fetch(`http://localhost:5000/api/books/${id}`)
      .then((res) => res.json())
      .then((data) => setBook(data))
      .catch((err) => console.error("Error fetching book:", err));
  }, [id]);

  // Function to delete the book
  const handleDelete = () => {
    fetch(`http://localhost:5000/api/books/${id}`, {
      method: 'DELETE',
    })
      .then((res) => res.json())
      .then(() => {
        alert("Book deleted successfully!");
        history.push('/books');  // Redirect to the list of books after deletion
      })
      .catch((err) => console.error("Error deleting book:", err));
  };

  // If no book is found, show loading
  if (!book) return <p>Loading...</p>;

  return (
    <div className="book-detail">
      <h2>{book.title}</h2>
      <p><strong>Author:</strong> {book.author}</p>
      <p><strong>Genre:</strong> {book.genre}</p>
      <p><strong>Publication Year:</strong> {book.publication_year}</p>

      {/* Edit and Delete buttons */}
      <div className="book-actions">
        <button onClick={() => history.push(`/edit-book/${id}`)}>Edit</button>
        <button onClick={() => setShowDeleteConfirm(true)}>Delete</button>
      </div>

      {/* Confirmation popup for deleting the book */}
      {showDeleteConfirm && (
        <div className="delete-confirmation">
          <p>Are you sure you want to delete this book?</p>
          <button onClick={handleDelete}>Yes, delete it</button>
          <button onClick={() => setShowDeleteConfirm(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
}
