import React from "react";
import "./MainPage.css";
import { useNavigate, Link } from "react-router-dom";

export default function MainPage() {
  const navigate = useNavigate();

  const bookCategories = [
    { src: "/public/fiction.jpg", url: "", label: "Fiction" },
    { src: "", url: "", label: "Science" },
    { src: "", url: "", label: "History" },
    { src: "", url: "", label: "Fantasy" },
    { src: "", url: "", label: "Biography" },
    { src: "", url: "", label: "Mystery" },
  ];

  const handleAdd = () => {
    navigate("/add-category");
  };

  return (
    <div className="main-container">
      <div className="header">
        <h1>Book Categories</h1>
        <button className="add-button" onClick={handleAdd}>Add</button>
      </div>

      <div className="category-grid">
        {bookCategories.map((item, index) => (
          <Link to={item.url} key={index} className="category-card">
            <img src={item.src} alt={item.label} />
            <p className="category-label">{item.label}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
